import React, { useState } from 'react';
import { 
  Bell, Search, MoreHorizontal, ChevronRight, ChevronDown, 
  Edit2, Plus, Save, X 
} from 'lucide-react';

const WorkflowInterface = () => {
    const initialSteps = [
        {
          id: 1,
          title: "Click On Gmail",
          description: undefined,
          imageUrl: undefined,
        },
        {
          id: 2,
          title: "Click on highlighted element",
          description: undefined,
          imageUrl: "https://hebbkx1anhila5yf.public.blob.vercel-storage.com/image-DB2p3uXkdnjIyf822ChWtY1IlNfMnj.png",
        },
        {
          id: 3,
          title: "Click on the button",
          description:
            "This action will navigate to the Gmail login page where you can access your email account. The system will automatically detect and highlight interactive elements for the next step in the workflow.",
          imageUrl: "https://hebbkx1anhila5yf.public.blob.vercel-storage.com/image-DB2p3uXkdnjIyf822ChWtY1IlNfMnj.png",
        },
      ];
  const [steps, setSteps] = useState(initialSteps);

  const [editingStep, setEditingStep] = useState(null);
  const [editForm, setEditForm] = useState(null);

  const handleEditStep = (step) => {
    setEditingStep(step.id);
    setEditForm({ ...step });
  };

  const handleSaveStep = () => {
    if (editForm) {
      setSteps(steps.map((step) => (step.id === editForm.id ? editForm : step)));
      setEditingStep(null);
      setEditForm(null);
    }
  };

  const handleCancelEdit = () => {
    setEditingStep(null);
    setEditForm(null);
  };

  const handleAddStep = (afterStepId) => {
    const newId = Math.max(...steps.map((s) => s.id)) + 1;
    const newStep = {
      id: newId,
      title: "New Step",
      description: undefined,
      imageUrl: undefined,
    };

    if (afterStepId === undefined) {
      // Add before first step
      setSteps([newStep, ...steps]);
    } else {
      // Add after specific step
      const index = steps.findIndex((s) => s.id === afterStepId);
      const newSteps = [...steps];
      newSteps.splice(index + 1, 0, newStep);
      setSteps(newSteps);
    }
  };

  return (
    <div className="flex bg-white text-black">
      {/* Sidebar */}
      <div className="fixed left-0 top-0 h-screen w-80 bg-slate-900 border-r border-slate-700 flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-slate-700">
          <div className="flex items-center gap-3">
            <div className="w-8 h-8 bg-orange-500 rounded-lg flex items-center justify-center text-white font-semibold">
              S
            </div>
            <span className="text-white font-medium">Siraar</span>
            <ChevronDown className="w-4 h-4 text-slate-400" />
          </div>
          <Bell className="w-5 h-5 text-slate-400" />
        </div>

        {/* Search */}
        <div className="p-4">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-slate-400" />
            <input
              placeholder="Search..."
              className="pl-10 w-full py-2 bg-slate-800 border border-slate-600 rounded text-slate-300 placeholder-slate-500"
            />
          </div>
        </div>

        {/* Navigation */}
        <div className="flex-1 px-4 space-y-6">
          <div className="text-sm text-slate-400 mb-4">PERSONAL DOCS</div>

          <div>
            <span className="text-slate-300">Spaces</span>
            <div className="flex items-center justify-between text-slate-300 hover:text-white cursor-pointer ml-4 space-y-1">
              <span className="text-white px-3 py-2 rounded text-sm font-medium">Main</span>
              <ChevronRight className="w-4 h-4" />
            </div>
          </div>

          <div className="border-t border-slate-700">
            <div className="text-sm text-slate-400 mb-4 mt-3">CHROME EXTENSION</div>
            <div className="space-y-2">
              <div className="flex items-center justify-between text-slate-300 hover:text-white cursor-pointer">
                <span>Captured Workflows</span>
                <ChevronDown className="w-4 h-4" />
              </div>
              <div className="ml-4 space-y-1">
                <div className="bg-orange-500 text-white px-3 py-2 rounded text-sm font-medium">
                  Capture 2025-08-20 → T19:00
                </div>
                <div className="text-slate-400 px-3 py-2 text-sm hover:text-white cursor-pointer">Untitled</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 ml-80 flex flex-col bg-white">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h1 className="text-xl font-semibold text-gray-900">Capture 2025-08-20 → T19:00</h1>
          <MoreHorizontal className="w-5 h-5 text-gray-400" />
        </div>

        {/* Workflow Steps */}
        <div className="p-6 space-y-6">
          <div className="relative">
            <div className="border-t border-gray-200"></div>
            <div className="absolute left-1/2 top-0 transform -translate-x-1/2 -translate-y-1/2">
              <button
                onClick={() => handleAddStep()}
                className="w-6 h-6 rounded-full bg-orange-500 hover:bg-orange-600 text-white p-0 flex items-center justify-center"
              >
                <Plus className="w-4 h-4" />
              </button>
            </div>
          </div>

          {steps.map((step, index) => (
            <div key={step.id} className="relative">
              {editingStep === step.id ? (
                // Edit mode
                <div className="space-y-4 p-4 border border-gray-900 rounded-lg bg-white">
                  <div className="flex items-center gap-4">
                    <div className="w-6 h-6 bg-orange-500 rounded-full flex items-center justify-center text-white text-sm font-medium">
                      {step.id}
                    </div>
                    <input
                      value={editForm?.title || ""}
                      onChange={(e) => setEditForm(editForm ? { ...editForm, title: e.target.value } : null)}
                      className="flex-1 bg-white border border-gray-900 text-gray-900 px-3 py-2 rounded"
                      placeholder="Step title"
                    />
                  </div>

                  <div className="ml-10 space-y-3">
                    <textarea
                      value={editForm?.description || ""}
                      onChange={(e) => setEditForm(editForm ? { ...editForm, description: e.target.value } : null)}
                      placeholder="Step description (optional)"
                      className="resize-none bg-white border border-gray-900 text-gray-900 px-3 py-2 rounded w-full"
                      rows={3}
                    />

                    <input
                      value={editForm?.imageUrl || ""}
                      onChange={(e) => setEditForm(editForm ? { ...editForm, imageUrl: e.target.value } : null)}
                      placeholder="Image URL (optional)"
                      className="bg-white border border-gray-900 text-gray-900 px-3 py-2 rounded w-full"
                    />

                    <div className="flex gap-2">
                      <button
                        onClick={handleSaveStep}
                        className="flex items-center bg-orange-500 text-white hover:bg-orange-600 px-3 py-1 rounded text-sm"
                      >
                        <Save className="w-4 h-4 mr-1" />
                        Save
                      </button>
                      <button
                        onClick={handleCancelEdit}
                        className="flex items-center border border-gray-900 text-white bg-gray-900 hover:bg-gray-700 hover:text-white px-3 py-1 rounded text-sm"
                      >
                        <X className="w-4 h-4 mr-1" />
                        Cancel
                      </button>
                    </div>
                  </div>
                </div>
              ) : (
                // View mode
                <div className="relative">
                  <div className="flex items-center justify-between gap-6">
                    {/* Left side (number, title, description) */}
                    <div className="flex flex-col gap-2 flex-1">
                      <div className="flex items-center gap-4">
                        <div className="w-6 h-6 bg-orange-500 rounded-full flex items-center justify-center text-white text-sm font-medium">
                          {step.id}
                        </div>
                        <span className="text-gray-900">
                          {step.title.includes("Gmail") ? (
                            <>
                              Click On <span className="underline">Gmail</span>
                            </>
                          ) : (
                            step.title
                          )}
                        </span>
                      </div>
                      {step.description && (
                        <div className="ml-10 text-sm text-gray-600 leading-relaxed max-w-md">{step.description}</div>
                      )}
                    </div>

                    {/* Right side (image) */}
                    {step.imageUrl && (
                      <div className="bg-gray-50 rounded-lg p-3 border border-gray-200">
                        <img
                          src={step.imageUrl || "/placeholder.svg"}
                          alt={`Screenshot for ${step.title}`}
                          className="w-104 rounded border border-gray-300"
                        />
                      </div>
                    )}
                  </div>

                  <button
                    onClick={() => handleEditStep(step)}
                    className="absolute bottom-0 right-0 text-gray-400 hover:text-gray-600 p-1"
                  >
                    <Edit2 className="w-4 h-4" />
                  </button>
                </div>
              )}

              {index < steps.length - 1 && (
                <div className="relative my-6">
                  <div className="border-t border-gray-200"></div>
                  <div className="absolute left-1/2 top-0 transform -translate-x-1/2 -translate-y-1/2">
                    <button
                      onClick={() => handleAddStep(step.id)}
                      className="w-6 h-6 rounded-full bg-orange-500 hover:bg-orange-600 text-white p-0 flex items-center justify-center"
                    >
                      <Plus className="w-4 h-4" />
                    </button>
                  </div>
                </div>
              )}
            </div>
          ))}

          <div className="relative">
            <div className="border-t border-gray-200"></div>
            <div className="absolute left-1/2 top-0 transform -translate-x-1/2 -translate-y-1/2">
              <button
                onClick={() => handleAddStep(steps[steps.length - 1]?.id)}
                className="w-6 h-6 rounded-full bg-orange-500 hover:bg-orange-600 text-white p-0 flex items-center justify-center"
              >
                <Plus className="w-4 h-4" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default WorkflowInterface;
