import React, { useState } from 'react';
import { FaSearch, FaBell, FaChevronDown, FaPlus, FaPen, FaChevronRight } from 'react-icons/fa';
import gmailScreenshot from './assets/gmail_screenshot.png'
const WorkflowCapture = () => {
  const [steps, setSteps] = useState([
    {
      id: 1,
      description: "Click On Gmail"
    },
    {
      id: 2,
      description: "Click on highlighted element",
      screenshot: gmailScreenshot,
    },
    {
        id:3,
        description:"Click on the Button",
        extra:'This action will navigate to the Gmail login page where you can access your email account. The system will automatically detect and highlight interactive elements for the next step in the workflow.',
        screenshot:gmailScreenshot,
    }
  ]);

  return (
    <div className="app">
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="user-avatar">
            <div className="avatar">S</div>
            <span className="username">Siraar</span>
            <FaChevronDown />
          </div>
          <div className="notification">
            <FaBell />
          </div>
        </div>

        <div className="search-container">
          <div className="search-box">
            <FaSearch className="search-icon" />
            <input type="text" placeholder="Search..." />
          </div>
        </div>

        <div className="sidebar-section">
        <h3 className='sidebar-title'>PERSONAL DOCS</h3>
          <div className="sidebar-menu">
            <h4 className='with-dropdown'>Spaces</h4>
            <div className="menu-item">
              <span>Main</span>
              <FaChevronRight />
            </div>
          </div>
        </div>

        <div className="sidebar-section">
          <h3 className='sidebar-title'>CHROME EXTENSION</h3>
          <div className="sidebar-menu">
            <h4 className="with-dropdown">Captured Workflows</h4>
            <div className="menu-item active">
              <span>Capture 2025-08-20 → T19:00</span>
            </div>
            <div className="menu-item">
              <span>Untitled</span>
            </div>
          </div>
        </div>
      </div>
      <div className="main-content">
      {/* Header */}
      <div className="content-header">
        <h2>Capture 2025-08-20 → T19:00</h2>
        <button className="more-options">...</button>
      </div>
      <div className="workflow-steps">
  {steps.map((step, index) => (
    <div key={step.id} className="step-wrapper">

      {/* Separator line with Add button (top) */}
      <div className="separator">
        <button className="add-step-btn">
          <FaPlus />
        </button>
      </div>

      {/* Step Content */}
      <div className="step-container">
        <div className="step-row">
          {/* Step Number + Description */}
          <div className="step-left">
            <div className="number-title">
              <div className="step-number">{step.id}</div>
              {step.description && <p className="step-description">{step.description}</p>}
            </div>

            {step.extra && <p className="step-extra">{step.extra}</p>}
          </div>

          {/* Screenshot */}
          {step.screenshot && (
            <div className="screenshot-container">
              <div className="screenshot">
                <img src={step.screenshot} alt={`Step ${step.id} screenshot`} />
              </div>
            </div>
          )}


        </div>
        <button className="edit-btn"><FaPen /></button>
      </div>
    </div>

        ))}

      </div>
      </div>

      <style jsx>{`
       .app {
        display: flex;
        height: 100vh;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
      }

      /* SIDEBAR STYLES */
      .sidebar {
        width: 300px;
        background-color: #111827; /* gray-900 */
        color: white;
        padding: 0; /* remove inner padding */
        display: flex;
        flex-direction: column;
        overflow-y: auto;
      }

      .sidebar-icon{
        color:grey;
      }
      /* HEADER */
      .sidebar-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 16px;
      }

      .user-avatar {
        display: flex;
        align-items: center;
        gap: 8px;
      }

      .avatar {
        width: 32px;
        height: 32px;
        background-color: #f97316; /* orange-500 */
        color: white;
        border-radius: 8px; /* smoother like screenshot */
        display: flex;
        align-items: center;
        justify-content: center;
        font-weight: bold;
      }

      /* SEARCH BOX */
      .search-container {
        padding: 0 16px;
        margin-bottom: 16px;
      }

      .search-box {
        background-color: #1f2937; /* gray-800 */
        border-radius: 8px;
        border:grey 0.5px solid;
        padding: 8px 12px;
        display: flex;
        align-items: center;
        gap: 8px;
      }

      .search-box input {
        background: transparent;
        border: none;
        color: white;
        width: 100%;
