        color: #9ca3af; /* gray-400 */
        margin-bottom: 8px;
        letter-spacing: 0.5px;
      }

      /* MENUS */
      .sidebar-menu h4 {
        font-size: 14px;
        font-weight: 600;
        color: #d1d5db; /* gray-300 */
        margin-bottom: 16px;
      }

      .with-dropdown {
        display: flex;
        align-items: center;
        justify-content: space-between;

      }

      /* MENU ITEMS */
      .menu-item {
        padding: 8px 22px;
