import React, { useState } from 'react';
import { FaSearch, FaBell, FaChevronDown, FaPlus, FaPen, FaChevronRight } from 'react-icons/fa';

const WorkflowCapture = () => {
  const [steps, setSteps] = useState([
    {
      id: 1,
      description: "Click On Gmail",
      screenshot: "gmail-screenshot.png"
    },
    {
      id: 2,
      description: "Click on highlighted element",
      screenshot: "gmail-highlighted.png"
    }
  ]);

  return (
    <div className="app">
      <div className="sidebar">
        <div className="sidebar-header">
          <div className="user-avatar">
            <div className="avatar">S</div>
            <span className="username">Siraar</span>
            <FaChevronDown />
          </div>
          <div className="notification">
            <FaBell />
          </div>
        </div>

        <div className="search-container">
          <div className="search-box">
            <FaSearch className="search-icon" />
            <input type="text" placeholder="Search..." />
          </div>
        </div>

        <div className="sidebar-section">
        <h3 className='sidebar-title'>PERSONAL DOCS</h3>
          <div className="sidebar-menu">
            <h4 className='with-dropdown'>Spaces</h4>
            <div className="menu-item">
              <span>Main</span>
              <FaChevronRight />
            </div>
          </div>
        </div>

        <div className="sidebar-section">
          <h3 className='sidebar-title'>CHROME EXTENSION</h3>
          <div className="sidebar-menu">
            <h4 className="with-dropdown">Captured Workflows</h4>
            <div className="menu-item active">
              <span>Capture 2025-08-20 → T19:00</span>
            </div>
            <div className="menu-item">
              <span>Untitled</span>
            </div>
          </div>
        </div>
      </div>

      <div className="main-content">
        <div className="content-header">
          <h2>Capture 2025-08-20 → T19:00</h2>
          <button className="more-options">More options</button>
        </div>

        <div className="workflow-steps">
          {steps.map((step, index) => (
            <div key={step.id} className="step-container">
              <div className="step-header">
                <div className="step-number">{step.id}</div>
                <div className="step-description">{step.description}</div>
                <button className="edit-btn"><FaPen /></button>
              </div>

              <button className="add-step-btn">
                <FaPlus />
              </button>

              {step.screenshot && (
                <div className="screenshot-container">
                  <div className="screenshot">
                    <img src={step.screenshot} alt={`Step ${step.id} screenshot`} />
                    <button className="edit-screenshot-btn"><FaPen /></button>
                  </div>
                </div>
              )}
            </div>
          ))}

          <button className="add-step-btn final">
            <FaPlus />
          </button>
        </div>
      </div>

      <style jsx>{`
       .app {
        display: flex;
        height: 100vh;
        font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
      }

      /* SIDEBAR STYLES */
      .sidebar {
        width: 300px;
        background-color: #111827; /* gray-900 */
        color: white;
        padding: 0; /* remove inner padding */
        display: flex;
        flex-direction: column;
        overflow-y: auto;
      }

      /* HEADER */
      .sidebar-header {
        display: flex;
        justify-content: space-between;
        align-items: center;
        padding: 16px;
      }

      .user-avatar {
        display: flex;
        align-items: center;
        gap: 8px;
      }

      .avatar {
        width: 32px;
        height: 32px;
        background-color: #f97316; /* orange-500 */
        color: white;
        border-radius: 8px; /* smoother like screenshot */
        display: flex;
        align-items: center;
        justify-content: center;
        font-weight: bold;
      }

      /* SEARCH BOX */
      .search-container {
        padding: 0 16px;
        margin-bottom: 16px;
      }

      .search-box {
        background-color: #1f2937; /* gray-800 */
        border-radius: 8px;
        padding: 8px 12px;
        display: flex;
        align-items: center;
        gap: 8px;
      }

      .search-box input {
        background: transparent;
        border: none;
        color: white;
        width: 100%;
        outline: none;
        font-size: 14px;
      }

      /* SECTION HEADERS */
      .sidebar-section {
        margin-bottom: 16px;
        padding: 0 16px;
      }
      .sidebar-title {
        text-align:left;
      }
      .sidebar-section h3 {
        font-size: 11px;
        font-weight: 600;
        color: #9ca3af; /* gray-400 */
        margin-bottom: 8px;
        letter-spacing: 0.5px;
      }

      /* MENUS */
      .sidebar-menu h4 {
        font-size: 14px;
        font-weight: 600;
        color: #d1d5db; /* gray-300 */
        margin-bottom: 6px;
      }

      .with-dropdown {
        display: flex;
        align-items: center;
        justify-content: space-between;
      }

      /* MENU ITEMS */
      .menu-item {
        padding: 8px 22px;
        border-radius: 6px;
        margin-bottom: 4px;
        display: flex;
        align-items: center;
        justify-content: space-between;
        cursor: pointer;
        font-size: 14px;
        color: #d1d5db; /* gray-300 */
      }

      .menu-item:hover {
        background-color: rgba(255, 255, 255, 0.05);
      }

      .menu-item.active {
        margin-left:12px;
        background-color: #f97316; /* orange-500 */
        color: white;
        font-weight: 500;
        padding-left:8px;
      }

      /* VERSION LABEL */
      .version {
        margin-top: auto;
        background-color: #1f2937; /* gray-800 */
        width: fit-content;
        padding: 4px 8px;
        border-radius: 6px;
        font-size: 12px;
        color: #9ca3af; /* gray-400 */
        margin: 16px;
      }

        /* MAIN CONTENT STYLES */
        .main-content {
          flex: 1;
          padding: 16px 24px;
          overflow-y: auto;
          background-color: white;
        }

        .content-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          padding-bottom: 16px;
          border-bottom: 1px solid #eee;
          margin-bottom: 24px;
        }

        .content-header h2 {
          font-size: 20px;
          font-weight: 500;
          color: #121212;
        }

        .more-options {
          background: none;
          border: none;
          color: #121212;
          cursor: pointer;
        }

        /* WORKFLOW STEPS */
        .workflow-steps {
          display: flex;
          flex-direction: column;
          gap: 16px;
        }

        .step-container {
          display: flex;
          flex-direction: column;
          gap: 12px;
        }

        .step-header {
          display: flex;
          align-items: center;
          gap: 12px;
        }

